package eu.bcvsolutions.idm.scripts.rules;
/**
 * code: GetUserRoles
 * name: GetUserRoles
 * desc: Returns a set of user's assigned role names.
 * authorities:
 * - eu.bcvsolutions.idm.core.api.dto.IdmIdentityRoleDto
 * - eu.bcvsolutions.idm.core.api.dto.IdmIdentityDto
 * - eu.bcvsolutions.idm.core.api.dto.IdmRoleDto
 * - java.util.HashSet
 * - SERVICE idmIdentityRoleService
 * - SERVICE roleService
 *
 * @author Jan Helbich
 */
//
//
//IdmIdentity -> entity
//IdmIdentityRoleService -> idmIdentityRoleService
//IdmRoleService -> roleService
//
org.slf4j.Logger LOG = org.slf4j.LoggerFactory.getLogger("GetUserRoles");

def roleNames = [];
def identityRoles = identityRoleService.findValidRole(entity.id, null).getContent();
identityRoles.each { role -> roleNames.add(roleService.get(role.role).name); }
//
List<String> resultRoles = new ArrayList<String>();
resultRoles.addAll(roleNames.toSet());
LOG.info("roles: {}", resultRoles);
return resultRoles;
